import React from "react";
import { useParams } from 'react-router-dom';
import { Col, Container, Row } from 'reactstrap';
import tourData from '../assets/data/tours';
import '../styles/tour-details.css';
import calculateAverageRating from "../utils/averageRating";

const TourDetails = () => {

    const {id} = useParams();



    const tour = tourData.find(tour => tour.id === id);

    const { photo, title, desc, price, address, reviews, city, distance, maxGroupSize} = 
    tour;

    const {totalRating, averageRating} = calculateAverageRating(reviews);

    return (
    <>
        <section>  
            <Container>
                <Row>
                    <Col lg="8">
                        <div className="tour__content">
                            <img src={photo} alt="" />

                            <div className="tour__info">
                                <h2>{title}</h2>

                                <div className="d-flex align-items-center gap-5">
                                <span className='tour__rating d-flex align-items-center gap-1'>
          <i class="ri-star-fill" style={{color:"var(--secondary-color)"}}></i> {calculateAverageRating === 0 ? null : averageRating}{totalRating === 0 ? ( 
            'Not rated' 
            ) : 
          (<span>({reviews.length})</span>)} 

          </span>

            <span>
            <i class="ri-map-pin-fill"></i>
            </span>

                                        </div>
                            </div>
                        </div>
                    </Col>
                </Row>
            </Container>

        </section>
    </>
    );
};

export default TourDetails;
